    BoundaryOpAttributes *a = ((BoundaryOpAttributesObject *)self)->data;
    BoundaryOpAttributes *b = ((BoundaryOpAttributesObject *)other)->data;

    switch (op)
    {
       case Py_EQ:
           res = (*a == *b) ? Py_True : Py_False;
           break;
       case Py_NE:
           res = (*a != *b) ? Py_True : Py_False;
           break;
       default:
           res = Py_NotImplemented;
           break;
    }

    Py_INCREF(res);
